<?php/** * @Add Meta Box For Rooms Post * @return * */add_action( 'add_meta_boxes', 'cs_rooms_capacity' );function cs_rooms_capacity(){  	add_meta_box( 'cs_rooms_capacity', __('Rooms Capacity Options', 'booking'), 'cs_meta_rooms_capacity', 'rooms_capacity', 'normal', 'high' );  }function cs_meta_rooms_capacity( $post ) {	global $post;	?>	<div class="page-wrap page-opts left">		<div class="option-sec" style="margin-bottom:0;">			<div class="opt-conts">				<div class="elementhidden">					<?php                     if ( function_exists( 'cs_room_options' ) ) {                         cs_rooms_capacity_options();                    }                    ?>				</div>                <script>					jQuery(document).ready(function($) {						cs_check_availabilty();					});				</script>			</div>		</div>	</div>	<div class="clear"></div>	<?php}/** * @Rooms options * @return html * */ if ( ! function_exists( 'cs_rooms_capacity_options' ) ) {	function cs_rooms_capacity_options() {				global $post, $cs_form_fields,$cs_plugin_options;				$limit	= isset( $cs_plugin_options['cs_total_rooms'] ) && $cs_plugin_options['cs_total_rooms'] !='' && absint($cs_plugin_options['cs_total_rooms']) ? $cs_plugin_options['cs_total_rooms'] : 100;		$cs_room_limit['']	= 'select No of Rooms';		for( $i=1; $i <= $limit;  $i++ ){			$cs_room_limit[$i]	= $i;		}						$cs_args = array( 'posts_per_page' => '-1', 'post_type' => 'rooms', 'orderby'=>'ID', 'post_status' => 'publish' );		$cust_query = get_posts($cs_args);		$cs_rooms   = array();		foreach( $cust_query as $type ) {			$cs_rooms[$type->ID] = get_the_title( $type->ID );		}		wp_reset_postdata();				$cs_form_fields->cs_form_select_render(			array(  'name'	=> __('Select Room', 'booking'),					'id'	=> 'room_id',					'classes' => '',					'std'	=> '',					'description'  => '',					'options'  => $cs_rooms,					'hint'  => ''				)		);						$cs_form_fields->cs_form_text_render(			array(  'name'		=> __('Room Price', 'booking'),					'id'		=> 'room_price',					'classes'   => '',					'std'		=> '',					'description'  =>__('Room Price will be applicable if room booking type will be set as per room from setting. Go here : Settings > General Settings > Booking Settings','booking'),					'hint'  => ''				)		);				$cs_form_fields->cs_form_text_render(			array(  'name'	=> __('Rooms Prefix', 'booking'),					'id'	=> 'rooms_prefix',					'classes' => '',					'std'	=> 'HT-',					'description'  => '',					'hint'  => ''				)		);				$cs_form_fields->cs_form_select_render(			array(  'name'	=> __('Number of Rooms', 'booking'),					'id'	=> 'room_num',					'classes' => '',					'std'	=> '',					'description'  => '',					'options'  => $cs_room_limit,					'hint'  => ''				)		);				$cs_form_fields->cs_form_text_render(			array(  'name'	=> __('Number of Adults', 'booking'),					'id'	=> 'room_max_adults',					'classes' => '',					'std'	=> '',					'description'  => '',					'hint'  => ''				)		);				$cs_form_fields->cs_form_text_render(			array(  'name'	=> __('Number of Children', 'booking'),					'id'	=> 'room_max_child',					'classes' => '',					'std'	=> '',					'description'  => '',					'hint'  => ''				)		);	}}?>