<?php/** * Room Listing Classic * */	global $room_excerpt,$category,$cs_notification,$cs_plugin_options;	$cs_plugin_notify = new CS_Plugin_Notification_Helper();	$width 		  = '202';	$height	      = '146';	$title_limit  = 46;	?><div class="cs-accomodation classicview">    <?php 		$query = new WP_Query( $args );		$post_count = $query->post_count;				if ( $query->have_posts() ) {  		  $postCounter    = 0;		  while ( $query->have_posts() )  : $query->the_post();              		  $thumbnail	  = '';		  $cs_post_id 	  = $post->ID;		  $cs_postObject  = get_post_meta(get_the_id(), "cs_array_data", true);		  $cs_gallery  	  = get_post_meta($post->ID, "cs_room_image_gallery", true);		  $cs_gallery	  = explode(',',$cs_gallery);		  		  if( is_array( $cs_gallery ) && count( $cs_gallery ) > 0 && $cs_gallery[0] !='' ){			 $thumbnail 	  =  CS_FUNCTIONS()->cs_get_post_img( $cs_gallery[0], $width, $height );		  }		  		  if( $thumbnail == '' ){		  	 $thumbnail		  = wp_hotel_booking::plugin_url().'/assets/images/no-image.png';		  }          		  $excerpt_data	= CS_FUNCTIONS()->cs_get_the_excerpt($room_excerpt,'false','Read More');		  ?>			<article class="col-md-12">              <figure><a href="<?php the_permalink();?>"><img src="<?php echo esc_url($thumbnail);?>" alt=""  /></a></figure>              <div class="accomodation-info">                <h2><a href="<?php the_permalink();?>"><?php echo cs_get_room_title(get_the_title(),$title_limit);?></a></h2>                  <?php cs_hotel_rating($cs_post_id,false); ?>				<?php 					if( isset( $room_excerpt ) && $room_excerpt > 0 ) {                		echo '<p>'.$excerpt_data.'</p>';					}                	cs_get_price_section( $cs_post_id);				?>                <ul class="cslist-info">				<?php 					$featureList = get_post_meta($cs_post_id, 'cs_room_features', true);					$cs_feature_options = isset($cs_plugin_options['cs_feats_options']) ? $cs_plugin_options['cs_feats_options'] : '';					$cs_output = '';					if( is_array($cs_feature_options) && sizeof($cs_feature_options) > 0 ) {						$counter	= 0;						foreach($cs_feature_options as $feature){ 							$feature_title 	 = $feature['cs_feats_title'];							$feature_image 	 = $feature['cs_feats_image'];							$feature_slug 	 = isset($feature['feats_id']) ? $feature['feats_id'] : '';							$checked		 = '';							$cs_image		 = '';							if( isset( $feature_image ) && $feature_image !='' ){								$cs_image	= '<img src="'.esc_url( $feature_image ).'" alt="" />';							}							else{								$cs_image	= '<i>&nbsp;</i>';							}							if ( is_array( $featureList ) && in_array( $feature_slug , $featureList )  ) {								$counter++;								if( $counter  < 4  ) {									echo '<li><a href="javascript:;">'.$cs_image.wp_trim_words( $feature_title, 3 ).'</a></li>';									if( $counter  == 3  ) {										echo ' <li><a href="'.get_the_permalink($cs_post_id).'"><i class="icon-plus8"></i>'.__('More','booking').'</a></li>';									}								}							}						}					}				?>                </ul>              </div>            </article>			<?php		  endwhile;		} else{			$cs_notification->error(__('No Rooms found','booking'));		}		?></div>