<?php/* * *@Shortcode Name : Room Search *@retrun * */if ( ! function_exists( 'cs_pb_room_search' ) ) {    function cs_pb_room_search($die = 0){        global $cs_node, $cs_count_node, $post;                $shortcode_element = '';        $filter_element = 'filterdrag';        $shortcode_view = '';        $output = array();        $PREFIX = 'cs_room_search';        $counter = $_POST['counter'];        $parseObject     = new ShortcodeParse();        $cs_counter = $_POST['counter'];        if ( isset($_POST['action']) && !isset($_POST['shortcode_element_id']) ) {            $POSTID = '';            $shortcode_element_id = '';        } else {            $POSTID = $_POST['POSTID'];            $shortcode_element_id = $_POST['shortcode_element_id'];            $shortcode_str = stripslashes ($shortcode_element_id);            $output = $parseObject->cs_shortcodes( $output, $shortcode_str , true , $PREFIX );        }                $defaults = array('search_title' => '','view' => '');            if(isset($output['0']['atts']))                $atts = $output['0']['atts'];            else                 $atts = array();            if(isset($output['0']['content']))                $atts_content = $output['0']['content'];            else                 $atts_content = array();            $search_element_size = '100';            foreach($defaults as $key=>$values){                if(isset($atts[$key]))                    $$key = $atts[$key];                else                     $$key =$values;             }			             $name = 'cs_pb_room_search';            $cs_count_node++;            $coloumn_class = 'column_100';                if(isset($_POST['shortcode_element']) && $_POST['shortcode_element'] == 'shortcode'){            $shortcode_element = 'shortcode_element_class';            $shortcode_view = 'cs-pbwp-shortcode';            $filter_element = 'ajax-drag';            $coloumn_class = '';        }				$rand_id = rand(45,897009);    ?><div id="<?php echo esc_attr($name.$cs_counter);?>_del" class="column  parentdelete <?php echo esc_attr($coloumn_class);?> <?php echo esc_attr($shortcode_view);?>" item="room_search" data="<?php echo cs_element_size_data_array_index($search_element_size)?>" >  <?php cs_element_setting($name,$cs_counter,$search_element_size,'','heart');?>  <div class="cs-wrapp-class-<?php echo esc_attr($cs_counter)?> <?php echo esc_attr($shortcode_element);?>" id="<?php echo esc_attr($name.$cs_counter)?>" data-shortcode-template="[cs_room_search {{attributes}}]" style="display: none;">    <div class="cs-heading-area">      <h5><?php _e('Edit Search Options','Booking');?></h5>      <a href="javascript:removeoverlay('<?php echo esc_attr($name.$cs_counter)?>','<?php echo esc_attr($filter_element);?>')" class="cs-btnclose"><i class="icon-times"></i></a> </div>      <div class="cs-pbwp-content">      <div class="cs-wrapp-clone cs-shortcode-wrapp cs-pbwp-content">        <ul class="form-elements">          <li class="to-label">            <label><?php _e('Title','Hotel');?></label>          </li>          <li class="to-field">            <input type="text" name="search_title[]" class="txtfield" value="<?php echo cs_allow_special_char($search_title)?>" />          </li>        </ul>       <ul class='form-elements'>          <li class='to-label'>            <label><?php _e('View','Hotel');?></label>          </li>          <li class='to-field select-style'>            <select name='view[]'>              <option value="default" <?php echo $view == 'default' ? 'selected': '';?>><?php _e('Default','Hotel');?></option>              <option value="full" <?php echo $view == 'full' ? 'selected': '';?>><?php _e('Full Width','Hotel');?></option>              <option value="fancy" <?php echo $view == 'fancy' ? 'selected': '';?>><?php _e('Fancy','Hotel');?></option>            </select>          </li>        </ul>      </div>      <?php if(isset($_POST['shortcode_element']) && $_POST['shortcode_element'] == 'shortcode'){?>      <ul class="form-elements insert-bg">        <li class="to-field"> <a class="insert-btn cs-main-btn" onclick="javascript:Shortcode_tab_insert_editor('<?php echo esc_js(str_replace('cs_pb_','',$name));?>','<?php echo esc_js($name.$cs_counter)?>','<?php echo esc_js($filter_element);?>')" ><?php _e('Insert','Hotel');?></a> </li>      </ul>      <div id="results-shortocde"></div>      <?php } else {?>      <ul class="form-elements noborder">        <li class="to-label"></li>        <li class="to-field">          <input type="hidden" name="cs_orderby[]" value="room_search" />          <input type="button" value="<?php _e('Save','Hotel');?>" style="margin-right:10px;" onclick="javascript:_removerlay(jQuery(this))" />        </li>      </ul>      <?php }?>    </div>    </div></div><?php        if ( $die <> 1 ) die();    }    add_action('wp_ajax_cs_pb_room_search', 'cs_pb_room_search');}?>