<?php/** * The template for displaying Footer */global $cs_theme_options;$cs_footer_back_to_top = isset($cs_theme_options['cs_footer_back_to_top']) ? $cs_theme_options['cs_footer_back_to_top'] : '';$cs_sub_footer_social_icons = isset($cs_theme_options['cs_sub_footer_social_icons']) ? $cs_theme_options['cs_sub_footer_social_icons'] : '';?></div></main><div class="clear"></div><!-- Footer --><?php$cs_footer_switch = isset($cs_theme_options['cs_footer_switch']) ? $cs_theme_options['cs_footer_switch'] : '';$cs_footer_widget = isset($cs_theme_options['cs_footer_widget']) ? $cs_theme_options['cs_footer_widget'] : '';if ((isset($cs_footer_switch) and $cs_footer_switch == 'on') || (isset($cs_footer_widget) and $cs_footer_widget == 'on')) {    ?>    <footer id="footer-sec">        <?php        if (isset($cs_footer_widget) and $cs_footer_widget == 'on') {            ?>            <div class="container">                <?php                $cs_footer_sidebar = (isset($cs_theme_options['cs_footer_widget_sidebar']) and $cs_theme_options['cs_footer_widget_sidebar'] <> "select sidebar") ? $cs_theme_options['cs_footer_widget_sidebar'] : 'footer-widget-1';                if (!function_exists('dynamic_sidebar') || !dynamic_sidebar($cs_footer_sidebar)) : endif;                ?>            </div>                            <?php } ?>                <!-- Bottom Section -->        <?php if (isset($cs_footer_switch) and $cs_footer_switch == 'on') { ?>             <div class="footer-content">                <div id="copyright">                    <div class="container">                        <div class="row">                            <div class="col-md-12">                                <?php                                $cs_sub_footer_menu = isset($cs_theme_options['cs_sub_footer_menu']) ? $cs_theme_options['cs_sub_footer_menu'] : '';                                if (isset($cs_sub_footer_menu) and $cs_sub_footer_menu == 'on') {                                    ?>                                    <nav class="footer-nav">                                        <?php                                        if (function_exists('cs_navigation')) {                                            cs_navigation('footer-menu', '', '', '1');                                        }                                        ?>                                    </nav>                                <?php } ?>                            </div>                            <?php if ($cs_footer_back_to_top == 'on') { ?>                                <div class="col-md-12">                                    <div class="footer-sepratore">                                        <span id="backtop"><i class="icon-arrow-up8 csbg-color"></i></span>                                    </div>                                </div>                            <?php } ?>                            <div class="col-md-6">                                <?php                                $cs_copy_right = isset($cs_theme_options['cs_copy_right']) ? $cs_theme_options['cs_copy_right'] : '';                                if (isset($cs_copy_right) and $cs_copy_right <> '') {                                    echo '<p>' . wp_specialchars_decode($cs_copy_right) . '</p>';                                } else {                                    echo '<p>&copy;' . gmdate("Y") . ' ' . get_option("blogname") . ' Wordpress ' . __('All rights reserved', 'luxury-hotel') . '.</p>';                                }                                ?>                            </div>                            <div class="col-md-6">                                <div class="social-media">                                    <ul>                                        <?php if ($cs_sub_footer_social_icons == 'on') { ?>                                            <?php                                            if (function_exists('cs_social_network')) {                                                cs_social_network();                                            }                                        }                                        ?>                                        <li><div class="footer_icon"><?php                                                if (function_exists('cs_footer_logo')) {                                                    cs_footer_logo();                                                }                                                ?></div></li>		                                    </ul>                                </div>                            </div>                        </div>                    </div>                 </div>            </div>        <?php } ?>    </footer>  <?php } ?><div class="clear"></div></div><!-- Wrapper End --> <?php wp_footer(); ?></body></html>