<?php/** * The template for displaying single post */global $post, $cs_theme_options;$cs_uniq = rand(11111111, 9999999);$cs_postObject = get_post_meta($post->ID, 'cs_full_data', true);$cs_gallery_ids = get_post_meta($post->ID, 'cs_post_list_gallery', true);$cs_gallery_slider_ids = get_post_meta($post->ID, 'cs_post_detail_gallery', true);$cs_gallery = explode(',', $cs_gallery_ids);$cs_gallery_slider = explode(',', $cs_gallery_slider_ids);get_header();$cs_layout = '';$leftSidebarFlag = false;$rightSidebarFlag = false;$cs_layout = get_post_meta($post->ID, 'cs_page_layout', true);$cs_sidebar_left = get_post_meta($post->ID, 'cs_page_sidebar_left', true);$cs_sidebar_right = get_post_meta($post->ID, 'cs_page_sidebar_right', true);$post_tags_show = get_post_meta($post->ID, 'cs_post_tags_show', true);$cs_post_social_sharing = get_post_meta($post->ID, 'cs_post_social_sharing', true);$post_pagination_show = get_post_meta($post->ID, 'cs_post_pagination_show', true);$inside_post_view = get_post_meta($post->ID, 'cs_detail_view', true);$post_audio = get_post_meta($post->ID, 'cs_post_detail_audio', true);$post_video = get_post_meta($post->ID, 'cs_post_detail_video', true);if ($cs_layout == "left") {    $cs_layout = "page-content";    $leftSidebarFlag = true;    $custom_height = 300;} else if ($cs_layout == "right") {    $cs_layout = "page-content";    $rightSidebarFlag = true;    $custom_height = 300;} else {    $cs_layout = "page-content-fullwidth";    $custom_height = 408;}if (!isset($cs_layout)) {    $cs_layout = isset($cs_theme_options['cs_single_post_layout']) ? $cs_theme_options['cs_single_post_layout'] : '';    if (isset($cs_layout) && $cs_layout == "sidebar_left") {        $cs_layout = "page-content";        $cs_sidebar_left = $cs_theme_options['cs_single_layout_sidebar'];        $leftSidebarFlag = true;        $custom_height = 300;    } else if (isset($cs_layout) && $cs_layout == "sidebar_right") {        $cs_layout = "page-content";        $cs_sidebar_right = $cs_theme_options['cs_single_layout_sidebar'];        $rightSidebarFlag = true;        $custom_height = 300;    }}$width = 818;$height = 460;?><div class="container">     <div class="row">        <?php if ($leftSidebarFlag == true) { ?>            <aside class="page-sidebar">                <?php if (!function_exists('dynamic_sidebar') || !dynamic_sidebar($cs_sidebar_left)) : ?>                <?php endif; ?>            </aside>            <?php        }        ?>        <div class="<?php echo esc_attr($cs_layout); ?>">             <?php            if (have_posts()):                while (have_posts()) : the_post();                    $cs_tags_name = 'post_tag';                    $cs_categories_name = 'category';                    $postname = 'post';                    $image_url = cs_get_post_img_src($post->ID, $width, $height);                    ?>                    <div class="blog-detail rich_editor_text">                         <div class="col-md-12">                            <article>                                <?php                                if (isset($inside_post_view) and $inside_post_view <> '') {                                    $viewType = '<i class="icon-camera"></i>';                                    if ($inside_post_view == "slider") {                                        $viewType = '<i class="icon-unsorted"></i>';                                        echo '<div class="cs-media"><figure class="detailpost">';                                        cs_post_flex_slider($width, $height, get_the_id(), 'post');                                        echo '</figure></div>';                                    } else if ($inside_post_view == "single" && $image_url <> '') {                                        $viewType = '<i class="icon-camera"></i>';                                        echo '<div class="cs-media"><figure class="detailpost">';                                        echo '<img src="' . $image_url . '" alt="img" >';                                        echo '</figure></div>';                                    } else if ($inside_post_view == "video" and $post_video <> '' and $inside_post_view <> '') {                                        $viewType = '<i class="icon-film"></i>';                                        $url = parse_url($post_video);                                        $SERVER_NAME = '';                                        if(function_exists('cs_glob_server')){                                            $SERVER_NAME =  cs_glob_server('SERVER_NAME');                                        }                                        if ($url['host'] == $SERVER_NAME) {                                            ?>                                            <?php                                            echo '<div class="cs-media"><figure class="detailpost">';                                            echo do_shortcode('[video width="' . $width . '" height="' . $height . '" src="' . $post_video . '"][/video]');                                            echo '</figure></div>';                                        } else if ($url['host'] == 'soundcloud.com') {                                            $width = '100%';                                            $video = wp_oembed_get($post_video, array('width' => $width, 'height' => $height));                                            $search = array('webkitallowfullscreen', 'mozallowfullscreen', 'frameborder="0"');                                            echo '<div class="px-soundcloud"><figure>';                                            echo str_replace($search, '', $video);                                            echo '</figure></div>';                                        } else {                                            if ($url['host'] == 'vimeo.com') {                                                echo '<div class="cs-media"><figure class="detailpost">';                                                $content_exp = explode("/", $post_video);                                                $content_vimo = array_pop($content_exp);                                                if (function_exists('html_i_frame')) {                                                 echo html_i_frame('', 0,$height,'','','','','','','https://player.vimeo.com/video/' . $content_vimo,'',$width,'webkitallowfullscreen mozallowfullscreen allowfullscreen');                                                }                                                echo '</div>';                                            } else {                                                echo '<div class="cs-media"><figure class="detailpost">';                                                $content = str_replace(array('watch?v=', 'http://www.dailymotion.com/'), array('embed/', '//www.dailymotion.com/embed/'), $post_video);                                                if (function_exists('html_i_frame')) {                                                    echo html_i_frame('', 0,$height,'','','','','','',$content,'',$width,'webkitallowfullscreen mozallowfullscreen allowfullscreen');                                                }                                                echo '</div>';                                            }                                        }                                    } elseif ($inside_post_view == "audio" and $inside_post_view <> '') {                                        $viewType = '<i class="icon-microphone"></i>';                                        echo '<div class="cs-media"><figure class="detailpost">';                                        echo do_shortcode('[audio mp3="' . $post_audio . '"][/audio]');                                        echo '</figure></div>';                                    }                                }                                ?>                                <section>                                    <?php                                    $categories_list = get_the_term_list(get_the_id(), 'category', '<li class="categories">', ', ', '</li>');                                    if ($categories_list) {                                        ?>                                        <ul class="post-options">                                            <?php printf('%1$s', $categories_list); ?>                                        </ul>                                        <?php                                    }                                    ?>                                    <h2><?php the_title(); ?></h2>                                    <div class="date-time">                                        <time datetime="<?php echo date_i18n('Y-m-d', strtotime(get_the_date())); ?>"><span><strong><?php echo date_i18n('d', strtotime(get_the_date())); ?></strong></span><small><?php echo date_i18n('M', strtotime(get_the_date())); ?></small></time>                                    </div>                                    <div class="blog-text">                                        <ul class="post-options">                                            <li><i class="icon-user9"></i> <?php _e('by', 'luxury-hotel') ?><span> <a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><?php echo get_the_author(); ?></a> </span></li>                                            <li><i class="icon-chat6"></i><?php comments_number('0 Comments', '1 Comment', '% Comments'); ?></li>                                            <li>                                                <?php                                                $cs_post_like_counter = get_post_meta(get_the_id(), "cs_post_like_counter", true);                                                if (!isset($cs_post_like_counter) or empty($cs_post_like_counter))                                                    $cs_post_like_counter = 0;                                                if (isset($_COOKIE["cs_post_like_counter" . get_the_id()])) {                                                    echo '<i class="icon-heart12"></i><a>' . __('Likes', 'luxury-hotel') . '<span> (' . $cs_post_like_counter . ')</span></a>';                                                } else {                                                    ?>                                                    <i class="icon-heart12"></i>                                                    <a id="post_likes<?php echo get_the_id(); ?>"                                                        onclick="javascript:cs_post_likes_count('<?php echo admin_url('admin-ajax.php') ?>', '<?php echo get_the_id() ?>')"><?php _e('Likes', 'luxury-hotel') ?>                                                        <span> (<?php echo absint($cs_post_like_counter) ?>)</span>                                                    </a>                                                    <?php                                                }                                                ?>                                            </li>                                        </ul>                                        <?php                                        the_content();                                        if ($post_tags_show) {                                            $post_tags_show_text = __('Tags', 'luxury-hotel');                                            $cs_tags_list = get_the_term_list(get_the_id(), 'post_tag', '<ul><li>', '</li><li>', '</ul>');                                            if ($cs_tags_list) {                                                ?>                                                <div class="cs-tags">                                                     <i class="icon-pricetags"></i>                                                    <div class="tags-inn">                                                        <h6><?php echo esc_html($post_tags_show_text) ?></h6>                                                        <?php printf('%1$s', $cs_tags_list); ?>                                                    </div>                                                </div>                                                <?php                                            }                                        }                                        $thumb_ID = get_post_thumbnail_id($post->ID);                                        if ($images = get_children(array(                                            'post_parent' => get_the_ID(),                                            'post_type' => 'attachment',                                            'exclude' => $thumb_ID,                                                ))) {                                            ?>                                            <div class="cs-attachments">                                                <h6><?php _e('Blog Attachments', 'luxury-hotel'); ?></h6>                                                <ul>                                                    <?php foreach ($images as $image) { ?>                                                                                <?php                                                        if ($image->post_mime_type == 'image/png' || $image->post_mime_type == 'image/gif' || $image->post_mime_type == 'image/jpg' || $image->post_mime_type == 'image/jpeg'                                                        ) {                                                            $image_url = cs_attachment_image_src((int) $image->ID, 150, 150);                                                            ?>                                                            <li>                                                                <figure>                                                                    <a href="<?php echo esc_url($image->guid); ?>">                                                                        <img src="<?php echo esc_url($image_url); ?>" alt="<?php echo esc_attr($image->post_name); ?>"></a>                                                                </figure>                                                            </li>                                                        <?php } else if ($image->post_mime_type == 'application/zip') { ?>                                                            <li>                                                                <figure>                                                                    <a href="<?php echo esc_url($image->guid); ?>"><i class="icon-file-zip-o"></i></a>                                                                </figure>                                                            </li>                                                        <?php } else if ($image->post_mime_type == 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') { ?>                                                            <li>                                                                <figure>                                                                    <a href="<?php echo esc_url($image->guid); ?>"><i class="icon-file-word-o"></i></a>                                                                </figure>                                                            </li>                                                        <?php } else if ($image->post_mime_type == 'text/plain') { ?>                                                            <li>                                                                <figure>                                                                    <a href="<?php echo esc_url($image->guid); ?>"><i class="icon-file-text"></i></a>                                                                </figure>                                                            </li>                                                        <?php } else if ($image->post_mime_type == 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') { ?>                                                            <li>                                                                <figure>                                                                    <a href="<?php echo esc_url($image->guid); ?>"><i class="icon-file-excel-o"></i></a>                                                                </figure>                                                            </li>                                                        <?php } else { ?>                                                            <li>                                                                <figure>                                                                    <a href="<?php echo esc_url($image->guid); ?>"><i class="icon-align-justify"></i></a>                                                                </figure>                                                            </li>                                                            <?php                                                        }                                                    }                                                    ?>                                                </ul>                                            </div>                                            <?php                                        }                                        if ($cs_post_social_sharing == "on") {                                            $post_social_sharing_text = __('Share Post', 'luxury-hotel');                                            echo '<div class="detail-post">                                                    <div class="socialmedia">';                                            cs_social_share_blog(false, true, $post_social_sharing_text);                                            echo '</div>                                                </div>';                                        }                                        ?>                                    </div>                                </section>                            </article>                        </div>                    </div>                    <?php                    if (isset($post_pagination_show) && $post_pagination_show == 'on') {                        cs_next_prev_custom_links('post');                    }                    comments_template('', true);                endwhile;            endif;            ?>        </div>        <?php        if ($rightSidebarFlag == true) {            ?>            <aside class="page-sidebar">                <?php if (!function_exists('dynamic_sidebar') || !dynamic_sidebar($cs_sidebar_right)) : endif; ?>            </aside>            <?php        }        ?>    </div></div><?phpget_footer();